def get_chat_admins(chat_id, token):
    response = requests.get(f'https://api.telegram.org/bot{token}/getChatAdministrators', params={'chat_id': chat_id})
    if response.status_code == 200:
        return [admin['user']['id'] for admin in response.json()['result']]
    else:
        print(f'Error getting chat admins: {response.status_code} {response.content}')
        return []

# функция мута игрока на 1 час 
def mute_player(player_id, chat_id):
    try:
        admins = get_chat_admins(chat_id, bot.token)
        if player_id in admins:
            bot.send_message(chat_id, "Администраторов группы я банить не могу")
            return None
        else:
            until_date = datetime.datetime.now() + datetime.timedelta(hours=1) 
            bot.restrict_chat_member(chat_id, player_id, until_date, can_send_messages=False) 
            return until_date
    except Exception as e:
        print(f'Error muting player: {e}')
        return None

# обработчик сообщений с матом 
@bot.message_handler(func=lambda message: any(word in message.text.lower() for word in mat_words)) 
def handle_mat_message(message):
    try:
        player_id = message.from_user.id 
        chat_id = message.chat.id
        until_date = mute_player(player_id, chat_id) 
        if until_date:
            bot.reply_to(message, f'❗Вы были замутены по причине: "2.15 Мат и завуалированный мат Открытый мат" на 1 час!                                                                                         ⭕                                                                                                                                                                 🕑Срок окончания мута - {until_date}.                                                                                                                                                                          📛                                                                                                                                          🗯Если это была ошибка, обратитесь к @Kirill_zharkov или @Sonua_9418 и пишите заявление на ошибку в муте.')
    except Exception as e:
        print(f'Error handling mat message: {e}')

 
# обработчик команды размута 
@bot.message_handler(commands=['unmute']) 
def handle_unmute_command(message):
    try:
        if message.reply_to_message is None:
            bot.reply_to(message, 'Эта команда должна быть вызвана в ответ на сообщение игрока.')
            return
        player_id = message.reply_to_message.from_user.id 
        bot.restrict_chat_member(message.chat.id, player_id, can_send_messages=True)
        bot.reply_to(message.reply_to_message, 'Следите за языком! 😜') 
    except Exception as e:
        print(f'Error handling unmute command: {e}')
 
while True:
    try:
        bot.polling(none_stop=True)
    except Exception as e:
        print(f'Error polling: {e}')
 
bot.polling()
